mapper.select:
    select 

    from fx_ydsj LEFT JOIN sys_department dept on sszd = dept.departmentid

	   	and zt = #{lock} 
	   	and substring(sfrq,1,length(#{st}))>=#{st}
	   	and substring(sfrq,1,length(#{et}))<=#{et}
	   	and substring(sfrq,1,length(#{st})) between #{st} and #{et}
	   	and lrymc like concat('%',#{lrymc},'%')
	   	and zdzz like concat('%',#{zdzz},'%')
	   	and sszd in (${depts})

   		order by ${order}
   	order by sfrq desc  
   		,dept.sortcode asc

SELECT count(*) FROM fx_ydsj where zt = 1
and substring(sfrq,1,length(#{sfrq}))=#{sfrq}
and xh!=#{xh}
and sszd = #{sszd}
